mapper.select:
SELECT p.prod_no, p.prod_name, p.image_file, p.prod_detail, p.manufacture_day, p.price, p.reg_date, p.stock-NVL(COUNT(t.tran_no),0) stock FROM product p, transaction t
p.prod_no=t.prod_no(+) AND p.prod_no=#{prodNo}
GROUP BY p.prod_no, p.prod_name, p.image_file, p.prod_detail, p.manufacture_day, p.price, p.reg_date, p.stock, t.prod_no

mapper.update:
UPDATE product
prod_name=#{prodName}, prod_detail=#{prodDetail}, manufacture_day=#{manuDate:VARCHAR}, price = null #{price}, image_file=#{fileName:VARCHAR}, stock=stock+ 0 #{stock}
WHERE prod_no=#{prodNo}
